mapper.select:
select sal.SRC_IP,sal.DST_IP,sal.EVENT_CONTENT,sii.IDC_NAME as TRANS_IDC_ID,ssw.WEBSITE_NAME as TRANS_WEB_CASE_RID, spea.Rid as SNAPSHOP, case WHEN spea.SNAPSHOP is not null and spea.SNAPSHOP!='' then '详情' else '无' end as TRANS_SNAPSHOP, case when src_ip is null or src_ip='' or src_ip ='0.0.0.0' then dst_ip ELSE src_ip end as IP, from SMMS_PENDING_EVENT as spea left join smms_idc_info as sii on spea.ACCESS_ID=sii.IDC_ID left join SMMS_WEB_CASE as ssw on spea.WEB_CASE_RID=ssw.RID left join SMMS_AF_LOG as sal on spea.LOG_RID=sal.RID
spea.RECORDSTATE='0'
${whereCondition}
order by ${order}

select count(*) NUM from SMMS_PENDING_EVENT as spea left join smms_idc_info as sii on spea.ACCESS_ID=sii.IDC_ID left join SMMS_AF_LOG as sal on spea.LOG_RID=sal.RID
spea.RECORDSTATE='0'
${whereCondition}

mapper.update:
update SMMS_PENDING_EVENT
EVENT_SOURCE=#{EVENT_SOURCE}, CLIENTNAME=#{CLIENTNAME}, REPORT_CITY=#{REPORT_CITY}, REPORT_TIME=#{REPORT_TIME}, OCCUR_TIME=#{OCCUR_TIME}, EVENT_TYPE1=#{EVENT_TYPE1}, EVENT_TYPE2=#{EVENT_TYPE2}, THREAT_LEVEL=#{THREAT_LEVEL}, THREAT_NAME=#{THREAT_NAME}, THREAT_TYPE1=#{THREAT_TYPE1}, THREAT_TYPE2=#{THREAT_TYPE2}, THREAT_TYPE3=#{THREAT_TYPE3}, THREAT_TYPE4=#{THREAT_TYPE4}, ATTACK_IP=#{ATTACK_IP}, CC_DOMAIN=#{CC_DOMAIN}, CC_URL=#{CC_URL}, IP=#{IP}, DOMAIN=#{DOMAIN}, URL=#{URL}, UNIT_INDUSTY=#{UNIT_INDUSTY}, UNIT_NAME=#{UNIT_NAME}, ACCESS_ID=#{ACCESS_ID}, ROOM_NAME=#{ROOM_NAME}, ROOM_IDX=#{ROOM_IDX}, SYS_RECTIFY_SUGGEST=#{SYS_RECTIFY_SUGGEST}, FINAL_RECTIFY_SUGGEST=#{FINAL_RECTIFY_SUGGEST}, IS_FORCE_CLOSE=#{IS_FORCE_CLOSE}, IS_WHITE_LIST=#{IS_WHITE_LIST}, FORCE_CLOSE_DESCE=#{FORCE_CLOSE_DESCE}, RECTIFY_TERM=#{RECTIFY_TERM}, CLOSE_TERM=#{CLOSE_TERM}, SEND_TIME=#{SEND_TIME}, SEND_TIMESTAMP=#{SEND_TIMESTAMP}, RECTIFY_STATE=#{RECTIFY_STATE}, RECTIFY_MEASURE=#{RECTIFY_MEASURE}, FEEDBACK_TIME=#{FEEDBACK_TIME}, FEEDBACK_TIMESTAMP=#{FEEDBACK_TIMESTAMP}, WEB_CASE_RID=#{WEB_CASE_RID}, MEMO=#{MEMO}, SNAPSHOP=#{SNAPSHOP}, LOG_TABLENAME=#{LOG_TABLENAME}, LOG_RID=#{LOG_RID}, MODIFIEDTIME=#{MODIFIEDTIME}, RECORDSTATE=#{RECORDSTATE},
where RID=#{RID}
