mapper.select:

	select  from SMMS_ROOM_IPRANGE 

		RECORDSTATE='0'

             ${whereCondition}



             order by ${order}


	select count(*) NUM  from SMMS_ROOM_IPRANGE 

		RECORDSTATE='0'

             ${whereCondition}

