mapper.update:

	update S_Attachment set                     
                        RECORDSTATE=#{RECORDSTATE}
	where RID=#{RID}

update S_Attachment
ATTACHMENTID = #{ATTACHMENTID}, ATTACHMENTNAME = #{ATTACHMENTNAME}, ARCHIVETYPE = #{ARCHIVETYPE}, ATTACHMENTREMARK = #{ATTACHMENTREMARK}, TABLEID = #{TABLEID}, RECORDRID = #{RECORDRID}, LIABDEPT = #{LIABDEPT}, LIABORG = #{LIABORG}, FILESIZE = #{FILESIZE}, FILEPATH = #{FILEPATH}, MODIFIEDTIME = #{MODIFIEDTIME}, MODIFIERID = #{MODIFIERID}, RECORDSTATE = #{RECORDSTATE}, ATTACHMENTSTATE = #{ATTACHMENTSTATE}
where RID =#{RID}


	update S_Attachment set                     
          ATTACHMENTSTATE = #{ATTACHMENTSTATE}
	where ATTACHMENTSTATE = '1'

mapper.select:
	update S_ATTACHMENT set                     
          MODIFIERID = #{MODIFIERID}, MODIFIEDTIME = #{MODIFIEDTIME}
	where RECORDRID = #{RECORDRID} and TABLEID = #{TABLEID} and RECORDSTATE = '0'
