mapper.select:

	select  from SMMS_IDC_INFO 

		RECORDSTATE='0'

             ${whereCondition}



             order by ${order}


	select count(*) NUM  from SMMS_IDC_INFO 

		RECORDSTATE='0'

             ${whereCondition}

