mapper.select:

	SELECT  FROM smms_web_case a LEFT JOIN smms_idc_info b ON a.ACCESS_ID = b.IDC_ID  

		a.RECORDSTATE='0'

             ${whereCondition}



             order by ${order}


	select count(*) NUM  from SMMS_WEB_CASE a LEFT JOIN smms_idc_info b ON a.ACCESS_ID = b.IDC_ID

		a.RECORDSTATE='0'

             ${whereCondition}

