mapper.select:
select mail.Rid as CONTENTPATH, case WHEN mail.CONTENTPATH is not null and mail.CONTENTPATH!='' then '详情' else '无' end as TRANS_CONTENTPATH, ,crc1.CRC_VALUE as TRANS_SERV_CRC,crc2.CRC_VALUE as TRANS_APP_CRC from SMMS_AC_MAIL as mail left join smms_crc_mapting as crc1 on mail.SERV_CRC = crc1.CRC_KEY left join smms_crc_mapting as crc2 on mail.APP_CRC = crc2.CRC_KEY
mail.RECORDSTATE='0' and (crc1.CRC_TYPE='a' or crc1.CRC_TYPE ='r' or crc1.CRC_TYPE ='t') and (crc2.CRC_TYPE='a' or crc2.CRC_TYPE ='r' or crc2.CRC_TYPE ='t')
${whereCondition}
order by ${order}

select count(*) NUM from SMMS_AC_MAIL as mail left join smms_crc_mapting as crc1 on mail.SERV_CRC = crc1.CRC_KEY left join smms_crc_mapting as crc2 on mail.APP_CRC = crc2.CRC_KEY
mail.RECORDSTATE='0' and (crc1.CRC_TYPE='a' or crc1.CRC_TYPE ='r' or crc1.CRC_TYPE ='t') and (crc2.CRC_TYPE='a' or crc2.CRC_TYPE ='r' or crc2.CRC_TYPE ='t')
${whereCondition}

mapper.update:
update SMMS_AC_MAIL
USERNAME=#{USERNAME}, GROUPNAME=#{GROUPNAME}, IP=#{IP}, SERV_CRC=#{SERV_CRC}, APP_CRC=#{APP_CRC}, LOG_TIME=#{LOG_TIME}, CONTENT=#{CONTENT}, TITLE=#{TITLE}, MAIL_FROM=#{MAIL_FROM}, MAIL_TO=#{MAIL_TO}, CONTENTPATH=#{CONTENTPATH}, RECORDSTATE=#{RECORDSTATE},
where RID=#{RID}
