mapper.select:
select sii.IDC_NAME as TRANS_IDC_ID,ssw.WEBSITE_NAME as TRANS_WEB_CASE_RID,ssp.Rid as SNAPSHOP, case WHEN ssp.SNAPSHOP is not null and ssp.SNAPSHOP!='' then '详情' else '无' end as TRANS_SNAPSHOP,DETIAL_COUNT as TRANS_RID , from SMMS_EVENT_MAIN as ssp left join smms_idc_info as sii on ssp.ACCESS_ID=sii.IDC_ID left join SMMS_WEB_CASE as ssw on ssp.WEB_CASE_RID=ssw.RID
ssp.RECORDSTATE='0'
${whereCondition}
order by ${order}

select count(*) NUM from SMMS_EVENT_MAIN as ssp left join smms_idc_info as sii on ssp.ACCESS_ID=sii.IDC_ID
ssp.RECORDSTATE='0'
${whereCondition}

mapper.update:
update SMMS_EVENT_MAIN
EVENT_SOURCE=#{EVENT_SOURCE}, CLIENTNAME=#{CLIENTNAME}, REPORT_CITY=#{REPORT_CITY}, EVENT_TYPE1=#{EVENT_TYPE1}, THREAT_LEVEL=#{THREAT_LEVEL}, THREAT_NAME=#{THREAT_NAME}, THREAT_TYPE1=#{THREAT_TYPE1}, THREAT_TYPE2=#{THREAT_TYPE2}, THREAT_TYPE3=#{THREAT_TYPE3}, THREAT_TYPE4=#{THREAT_TYPE4}, ACCESS_ID=#{ACCESS_ID}, ROOM_NAME=#{ROOM_NAME}, ROOM_IDX=#{ROOM_IDX}, DOMAIN_NAME=#{DOMAIN_NAME}, CC_DOMAIN=#{CC_DOMAIN}, IP=#{IP}, ATTACK_IP=#{ATTACK_IP}, SYS_RECTIFY_SUGGEST=#{SYS_RECTIFY_SUGGEST}, FINAL_RECTIFY_SUGGEST=#{FINAL_RECTIFY_SUGGEST}, IS_FORCE_CLOSE=#{IS_FORCE_CLOSE}, IS_WHITE_LIST=#{IS_WHITE_LIST}, FORCE_CLOSE_DESC=#{FORCE_CLOSE_DESC}, RECTIFY_TERM=#{RECTIFY_TERM}, CLOSE_TERM=#{CLOSE_TERM}, SEND_TIME=#{SEND_TIME}, SEND_TIMESTAMP=#{SEND_TIMESTAMP}, RECTIFY_STATE=#{RECTIFY_STATE}, RECTIFY_MEASURE=#{RECTIFY_MEASURE}, FEEDBACK_TIME=#{FEEDBACK_TIME}, FEEDBACK_TIMESTAMP=#{FEEDBACK_TIMESTAMP}, WEB_CASE_RID=#{WEB_CASE_RID}, MAPPING_MODE=#{MAPPING_MODE}, MEMO=#{MEMO}, MODIFIEDTIME=#{MODIFIEDTIME}, ENFORCE_COUNT=#{ENFORCE_COUNT}, PRIORITY=#{PRIORITY}, RECORDSTATE=#{RECORDSTATE},
where RID=#{RID}
