mapper.select:

	select  from SMMS_EVENT_MAIN 

		RECORDSTATE='0'

             ${whereCondition}



             order by ${order}


	select count(*) NUM  from SMMS_EVENT_MAIN 

		RECORDSTATE='0'

             ${whereCondition}

