mapper.select:
select count(*) NUM from SMMS_PENDING_EVENT as ssp left join smms_idc_info as sii on ssp.ACCESS_ID=sii.IDC_ID
ssp.RECORDSTATE='0'
${whereCondition}

mapper.update:
update SMMS_PENDING_EVENT set 
RECTIFY_MEASURE=#{RECTIFY_MEASURE},
RECTIFY_STATE=#{RECTIFY_STATE},
FEEDBACK_TIME=#{FEEDBACK_TIME},
FEEDBACK_TIMESTAMP=#{FEEDBACK_TIMESTAMP}
where RID=#{RID}

update SMMS_PENDING_EVENT
CLIENTNAME=#{CLIENTNAME}, MAIN_RID=#{MAIN_RID}, REPORT_CITY=#{REPORT_CITY}, REPORT_TIME=#{REPORT_TIME}, OCCUR_TIME=#{OCCUR_TIME}, EVENT_TYPE1=#{EVENT_TYPE1}, EVENT_TYPE2=#{EVENT_TYPE2}, EVENT_TYPE2=#{EVENT_TYPE3}, THREAT_LEVEL=#{THREAT_LEVEL}, THREAT_NAME=#{THREAT_NAME}, THREAT_TYPE1=#{THREAT_TYPE1}, THREAT_TYPE2=#{THREAT_TYPE2}, THREAT_TYPE3=#{THREAT_TYPE3}, THREAT_TYPE4=#{THREAT_TYPE4}, ATTACK_IP=#{ATTACK_IP}, CC_DOMAIN=#{CC_DOMAIN}, CC_URL=#{CC_URL}, IP=#{IP},
KEYWORDS=#{KEYWORDS}, DOMAIN_NAME=#{DOMAIN_NAME}, UNIT_INDUSTY=#{UNIT_INDUSTY}, UNIT_NAME=#{UNIT_NAME}, MEMO=#{MEMO}, ACCESS_ID=#{ACCESS_ID}, ROOM_NAME=#{ROOM_NAME}, ROOM_IDX=#{ROOM_IDX}, MODIFIEDTIME=#{MODIFIEDTIME}, RECORDSTATE=#{RECORDSTATE}, RECTIFY_MEASURE=#{RECTIFY_MEASURE}, FEEDBACK_TIME=#{FEEDBACK_TIME}, FEEDBACK_TIMESTAMP=#{FEEDBACK_TIMESTAMP}, SYS_RECTIFY_SUGGEST=#{SYS_RECTIFY_SUGGEST}, FINAL_RECTIFY_SUGGEST=#{FINAL_RECTIFY_SUGGEST}, RECTIFY_TERM=#{RECTIFY_TERM}, CLOSE_TERM=#{CLOSE_TERM}, SEND_TIME=#{SEND_TIME}, SEND_TIMESTAMP=#{SEND_TIMESTAMP}, RECTIFY_STATE=#{RECTIFY_STATE}, EVENT_SOURCE=#{EVENT_SOURCE}, IS_FORCE_CLOSE=#{IS_FORCE_CLOSE}, WEB_CASE_RID=#{WEB_CASE_RID}, MAPPING_MODE=#{MAPPING_MODE}, IS_WHITE_LIST=#{IS_WHITE_LIST}, FORCE_CLOSE_DESC=#{FORCE_CLOSE_DESC}, SNAPSHOP=#{SNAPSHOP}, LOG_RID=#{LOG_RID}, LOG_FILENAME=#{LOG_FILENAME}, LOG_TABLENAME=#{LOG_TABLENAME}, PRIORITY=#{PRIORITY}, URL=#{URL}
where RID=#{RID}

UPDATE SMMS_PENDING_EVENT

		     RECTIFY_STATE=#{RECTIFY_STATE}, 
		     SEND_TIME=#{SEND_TIME}, 
		     SEND_TIMESTAMP=#{SEND_TIMESTAMP} 

WHERE RID =#{event_id} and RECTIFY_STATE='000' and IS_FORCE_CLOSE='2' ;


update SMMS_PENDING_EVENT   

	     RECTIFY_STATE=#{RECTIFY_STATE}, 
	     SEND_TIME=#{SEND_TIME}, 
	     SEND_TIMESTAMP=#{SEND_TIMESTAMP} 

where RID=#{event_id} and RECTIFY_STATE='000' and IS_FORCE_CLOSE='1'and RECORDSTATE='0';
