mapper.select:
select , sii.IDC_NAME as TRANS_IDC_ID from SMMS_ROOM_INFO as ssr left JOIN smms_idc_info as sii on ssr.ACCESS_ID=sii.IDC_ID
ssr.RECORDSTATE='0'
${whereCondition}
order by ${order}


	select count(*) NUM  from SMMS_ROOM_INFO as ssr left JOIN smms_idc_info as sii on ssr.ACCESS_ID=sii.IDC_ID

		ssr.RECORDSTATE='0'

             ${whereCondition}

SELECT ssi.ROOM_NAME as ROOM_NAME,ssi.ROOM_IDX as ROOM_IDX FROM 
		smms_room_info as ssi 
 where ssi.RECORDSTATE='0' and ssi.ACCESS_ID=#{ACCESS_ID} and ssi.ROOM_IDX=#{ROOM_IDX}


select  from SMMS_ROOM_INFO  as ssr 
left JOIN smms_idc_info as sii on ssr.ACCESS_ID=sii.IDC_ID
where ssr.RID = #{RID}

mapper.update:
update SMMS_ROOM_INFO ssr
ACCESS_ID=#{ACCESS_ID}, ROOM_NAME=#{ROOM_NAME}, ROOM_IDX=#{ROOM_IDX}, ROOM_ADDRESS=#{ROOM_ADDRESS}, ROOM_PROPERTY=#{ROOM_PROPERTY}, ROOM_MANAGER=#{ROOM_MANAGER}, MANAGER_TEL=#{MANAGER_TEL}, MANAGER_MOBILE=#{MANAGER_MOBILE}, MODIFIEDTIME=#{MODIFIEDTIME}, RECORDSTATE=#{RECORDSTATE},
where ssr.RID=#{RID}
