mapper.select:

	select  from SMMS_WEB_CASE_IP 

		RECORDSTATE='0'

             ${whereCondition}



             order by ${order}


	select count(*) NUM  from SMMS_WEB_CASE_IP 

		RECORDSTATE='0'

             ${whereCondition}

mapper.update:
update SMMS_WEB_CASE_IP
CREATTIME=#{CREATTIME}, MODIFIEDTIME=#{MODIFIEDTIME}, RECORDSTATE=#{RECORDSTATE}, CASE_RID=#{CASE_RID}, ACCESS_IP=#{ACCESS_IP},
where RID=#{RID}
