mapper.select:
select ,sii.IDC_NAME as TRANS_IDC_ID,ssr.ROOM_NAME as TRANS_ROOM_IDX from SMMS_DOMAIN_FULX flx left join SMMS_ROOM_INFO as ssr on flx.ROOM_IDX = ssr.ROOM_IDX and flx.IDC_ID = ssr.ACCESS_ID and ssr.RECORDSTATE='0' left join smms_idc_info as sii on flx.IDC_ID = sii.IDC_ID and ssr.RECORDSTATE='0'
flx.RECORDSTATE='0'
${whereCondition}
order by ${order}


	select count(*) NUM  from SMMS_DOMAIN_FULX flx

		flx.RECORDSTATE='0'

             ${whereCondition}

mapper.update:
update SMMS_DOMAIN_FULX
RID=#{RID}, IDC_ID=#{IDC_ID}, ROOM_IDX=#{ROOM_IDX}, DOMAIN=#{DOMAIN}, VISI_COUNT=#{VISI_COUNT}, TOTAL_FLUX=#{TOTAL_FLUX}, UP_FLUX=#{UP_FLUX}, DOWN_FLUX=#{DOWN_FLUX}, PERCENT=#{PERCENT}, STAT_YEAR=#{STAT_YEAR}, STAT_MONTH=#{STAT_MONTH}, STAT_DATE=#{STAT_DATE}, MODIFIEDTIME=#{MODIFIEDTIME}, CREATTIME=#{CREATTIME}, RECORDSTATE=#{RECORDSTATE},
where RID=#{RID}
