mapper.select:

select LOG_COUNT from smms_rpt_record
	 where IDC_ID=#{ACCESS_ID} 
	 	and ROOM_IDX=#{ROOM_IDX} 
	 	and LOG_FLAG=#{LOG_FLAG}
	 	and STAT_YEAR=#{STAT_YEAR}
        and STAT_MONTH=#{STAT_MONTH}
        and	STAT_DATE=#{STAT_DATE}

mapper.update:

	update SMMS_RPT_RECORD set LOG_COUNT=#{LOG_COUNT},MODIFIEDTIME=#{MODIFIEDTIME}
    where IDC_ID=#{ACCESS_ID} 
    and ROOM_IDX=#{ROOM_IDX} 
    and LOG_FLAG=#{LOG_FLAG} 
    and STAT_YEAR=#{STAT_YEAR}
	and STAT_MONTH=#{STAT_MONTH}
	and	STAT_DATE=#{STAT_DATE}

update SMMS_RPT_RECORD
RID=#{RID}, IDC_ID=#{IDC_ID}, ROOM_IDX=#{ROOM_IDX}, LOG_FLAG=#{LOG_FLAG}, STAT_YEAR=#{STAT_YEAR}, STAT_MONTH=#{STAT_MONTH}, STAT_DATE=#{STAT_DATE}, LOG_COUNT=#{LOG_COUNT}, MODIFIEDTIME=#{MODIFIEDTIME}, CREATTIME=#{CREATTIME}, RECORDSTATE=#{RECORDSTATE}
where RID=#{RID}
