mapper.select:
select ,crc1.CRC_VALUE as TRANS_SERV_CRC, crc2.CRC_VALUE as TRANS_APP_CRC from SMMS_AC_APP as app left join smms_crc_mapting as crc1 on app.SERV_CRC = crc1.CRC_KEY left join smms_crc_mapting as crc2 on app.APP_CRC = crc2.CRC_KEY
app.RECORDSTATE='0' and (crc1.CRC_TYPE='a' or crc1.CRC_TYPE ='r' or crc1.CRC_TYPE ='t') and (crc2.CRC_TYPE='a' or crc2.CRC_TYPE ='r' or crc2.CRC_TYPE ='t')
${whereCondition}
order by ${order}

select count(*) NUM from SMMS_AC_APP as app left join smms_crc_mapting as crc1 on app.SERV_CRC = crc1.CRC_KEY left join smms_crc_mapting as crc2 on app.APP_CRC = crc2.CRC_KEY
app.RECORDSTATE='0' and (crc1.CRC_TYPE='a' or crc1.CRC_TYPE ='r' or crc1.CRC_TYPE ='t') and (crc2.CRC_TYPE='a' or crc2.CRC_TYPE ='r' or crc2.CRC_TYPE ='t')
${whereCondition}

mapper.update:
update SMMS_AC_APP
USERNAME=#{USERNAME}, GROUPNAME=#{GROUPNAME}, IP=#{IP}, SERV_CRC=#{SERV_CRC}, APP_CRC=#{APP_CRC}, LOG_TIME=#{LOG_TIME}, DES_IP=#{DES_IP}, DES_PORT=#{DES_PORT}, RECORDSTATE=#{RECORDSTATE},
where RID=#{RID}
